import styled from "styled-components";

export const InputStyle = styled.div`
  width: 500px;
  height: 200px;
  display: flex;
  flex-direction: row;
  align-items: space-between;
  align-items: center;
  margin-top: 20px;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: 30px 30px;

  input {
    width: 90%;
    height: 35px;
    border-radius: 5px;
    border: none;
    font-size: 15pt;
    border: 1px solid gray;
    grid-gap: 20px;
  }

  input[name="task"] {
    grid-column: 1/3;
    background: red;
    width: 95%;
    margin-bottom: 20px;
  }

  button {
    font-size: 13pt;
    height: 100%;
    width: 90%;
    background: violet;
    border: none;
    color: white;
    border-radius: 5px;
  }
`;
