import { createContext, useState } from "react";

export const todoContext = createContext([]);

interface IProps {
  children: any;
}

interface ITodo {
  task: string;
  isDone: boolean;
}

export const ContextProvider = ({ children }: IProps) => {
  const [todos, setToDo] = useState<Array<ITodo>>([]);

  return (
    <todoContext.Provider value={[todos, setToDo]}>
      {children}
    </todoContext.Provider>
  );
};
