import styled from "styled-components";

export const ListStyle = styled.ul`
  width: 500px;
  height: auto;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  list-style: none;
  box-sizing: border-box;

  background: red;
`;

export const Task = styled.li`
  width: 100%;
  height: auto;
  padding-top: 20px;
  padding-bottom: 20px;
  border-radius: 5px;
  align-text: center;
  background: rgba(0, 0, 0, 0.3);
  box-sizing: border-box;
  padding-left: 10px;
`;

export const StyledTask = ({ children }) => {
  return { children };
};
