import styled from "styled-components";

export const InputStyle = styled.div`
  width: 500px;
  height: 50px;
  display: flex;
  flex-direction: row;
  align-items: space-between;
  align-items: center;

  input {
    width: 80%;
    height: 99%;
    border-radius: 5px;
    border: none;
    border: 1px solid gray;
  }

  button {
    font-size: 15pt;
    font-weight: 1000;
    height: 100%;
    width: 50px;
    border: none;
  }
`;
