import styled from "styled-components";

export const InputStyle = styled.div`
  width: 500px;
  height: 50px;
  display: flex;
  flex-direction: row;
  align-items: space-between;
  align-items: center;
  margin-top: 20px;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: 30px 30px;

  input {
    width: 80%;
    height: 95%;
    border-radius: 5px;
    border: none;
    font-size: 15pt;
    border: 1px solid gray;
  }

  input[name="task"] {
    grid-column: 1/2;
    background: red;
  }

  button {
    font-size: 20pt;
    font-weight: 1000;
    height: 100%;
    width: 50px;
    border: none;
  }
`;
