import styled from "styled-components";

export const InputStyle = styled.div`
  width: 500px;
  height: auto;
  margin-top: 50px;

  display: grid;
  background: red;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: 30px 30px;

  input {
    width: 95%;
    height: 35px;
    border-radius: 5px;
    border: none;
    font-size: 15pt;
    border: 1px solid gray;
  }

  input[name="task"] {
    grid-column: 1/3;
    background: red;
    width: 97%;
    margin-top: 20px;
  }

  button {
    font-size: 13pt;
    height: 35px;
    width: 100%;
    background: violet;
    border: none;
    color: white;
    border-radius: 5px;
  }
`;
